using Microsoft.VisualStudio.TestTools.UnitTesting;
using System.Net.Http;
using System.Threading.Tasks;
using System.Net;

namespace Tests.RouteTests
{
    [TestClass]
    public class OrderDetailsCoreTests : WebApiTestBaseClass
    {
        [TestMethod]
        public async Task OrderDetailsGetShouldReturnHTTPOk()
        {
            using (var response = await HttpClient.GetAsync("http://testserver/api/OrderDetails?%24top=10"))
            {
                var result = await response.Content.ReadAsStringAsync();
                Assert.IsTrue(response.StatusCode == HttpStatusCode.OK, "Return Get 10 or less for OrderDetails");
            }


            using (var response = await HttpClient.GetAsync("http://testserver/api/OrderDetails?%24expand=Order&%24top=10"))
            {
                var result = await response.Content.ReadAsStringAsync();
                Assert.IsTrue(response.StatusCode == HttpStatusCode.OK, "Return Get 10 or less OrderDetails with Expand of Order");
            }

            using (var response = await HttpClient.GetAsync("http://testserver/api/OrderDetails?%24expand=Product&%24top=10"))
            {
                var result = await response.Content.ReadAsStringAsync();
                Assert.IsTrue(response.StatusCode == HttpStatusCode.OK, "Return Get 10 or less OrderDetails with Expand of Product");
            }
 
        }
    }
}
