using Microsoft.VisualStudio.TestTools.UnitTesting;
using System.Net.Http;
using System.Threading.Tasks;
using System.Net;

namespace Tests.RouteTests
{
    [TestClass]
    public class ProductsCoreTests : WebApiTestBaseClass
    {
        [TestMethod]
        public async Task ProductsGetShouldReturnHTTPOk()
        {
            using (var response = await HttpClient.GetAsync("http://testserver/api/Products?%24top=10"))
            {
                var result = await response.Content.ReadAsStringAsync();
                Assert.IsTrue(response.StatusCode == HttpStatusCode.OK, "Return Get 10 or less for Products");
            }


            using (var response = await HttpClient.GetAsync("http://testserver/api/Products?%24expand=Category&%24top=10"))
            {
                var result = await response.Content.ReadAsStringAsync();
                Assert.IsTrue(response.StatusCode == HttpStatusCode.OK, "Return Get 10 or less Products with Expand of Category");
            }

            using (var response = await HttpClient.GetAsync("http://testserver/api/Products?%24expand=Supplier&%24top=10"))
            {
                var result = await response.Content.ReadAsStringAsync();
                Assert.IsTrue(response.StatusCode == HttpStatusCode.OK, "Return Get 10 or less Products with Expand of Supplier");
            }
 
        }
    }
}
