    [TestClass]
    public class StateProvincesCoreTests : WebApiTestBaseClass
    {
        [TestMethod]
        public async Task StateProvincesGetShouldReturnHTTPOk()
        {
            using (var response = await HttpClient.GetAsync("http://testserver/api/StateProvinces?%24top=10"))
            {
                var result = await response.Content.ReadAsStringAsync();
                Assert.IsTrue(response.StatusCode == HttpStatusCode.OK, "Return Get 10 or less for StateProvinces");
            }


            using (var response = await HttpClient.GetAsync("http://testserver/api/StateProvinces?%24expand=Country&%24top=10"))
            {
                var result = await response.Content.ReadAsStringAsync();
                Assert.IsTrue(response.StatusCode == HttpStatusCode.OK, "Return Get 10 or less StateProvinces with Expand of Country");
            }

            using (var response = await HttpClient.GetAsync("http://testserver/api/StateProvinces?%24expand=Person&%24top=10"))
            {
                var result = await response.Content.ReadAsStringAsync();
                Assert.IsTrue(response.StatusCode == HttpStatusCode.OK, "Return Get 10 or less StateProvinces with Expand of Person");
            }
 
        }
    }
